mapper.select:
select mid, name, slug, type, contentType, description, sort, parentId
from t_meta
where mid = #{mid,jdbcType=INTEGER}

select
a.*,
count(b.cid) as count
from  AS a
left join  AS b on a.mid = b.mid
where a.type = #{type}
group by a.mid
order by #{order}
limit #{limit}

mapper.insert:
insert into
name,slug,type,description,sort,parentId
#{name,jdbcType=VARCHAR}, #{slug,jdbcType=VARCHAR},#{type, jdbcType=VARCHAR}, #{description, jdbcType=VARCHAR}, #{sort, jdbcType=INTEGER}, #{parentId, jdbcType=INTEGER}

mapper.select:
select

from
 as m


    and m.name = #{name,jdbcType=VARCHAR}


    and m.type = #{type,jdbcType=VARCHAR}


order by m.sort
